CREATE TABLE accounts (
    id INT NOT NULL PRIMARY KEY,
    username VARCHAR2(50) NOT NULL,
    pass VARCHAR2(50) NOT NULL,
    email VARCHAR2(50) NOT NULL,
    coins NUMBER(6),
    created_at DATE,
    updated_at DATE,
    CONSTRAINT unique_email UNIQUE (email),
    CONSTRAINT unique_username UNIQUE (username)
)
/

CREATE TABLE items (
    id INT NOT NULL PRIMARY KEY,
    item_name VARCHAR2(50) NOT NULL, 
    class VARCHAR2(50),
    typeof VARCHAR2(50),
    wear VARCHAR2(50),
    rarity VARCHAR2(50),
    price NUMBER(5,2),
    added_at DATE,
    updated_at DATE  
)
/

CREATE TABLE games (
    id INT NOT NULL PRIMARY KEY,
    title VARCHAR2(50) NOT NULL,
    price NUMBER(5,2),
    launch_date DATE NOT NULL,
    added_at DATE,
    updated_at DATE
)
/

CREATE TABLE specific_items (
    id_game INT NOT NULL,
    id_item INT NOT NULL,
    added_at DATE,
    updated_at DATE,
    CONSTRAINT fk_specific_games_id_game FOREIGN KEY (id_game) REFERENCES games(id),
    CONSTRAINT fk_specific_items_id_item FOREIGN KEY (id_item) REFERENCES items(id),
    CONSTRAINT pk_specific_items PRIMARY KEY (id_game,id_item)
)
/

CREATE TABLE owned_items (
    id_owner INT NOT NULL,
    id_item INT NOT NULL,
    added_at DATE,
    updated_at DATE,
    CONSTRAINT fk_owned_items1 FOREIGN KEY (id_owner) REFERENCES accounts(id),
    CONSTRAINT fk_owned_items2 FOREIGN KEY (id_item) REFERENCES items(id),
    CONSTRAINT pk_owned_items PRIMARY KEY (id_owner,id_item)    
)
/

CREATE TABLE owned_games ( 
    id_owner INT NOT NULL,
    id_game INT NOT NULL,
    added_at DATE,
    updated_at DATE,
    CONSTRAINT fk_owned_games1 FOREIGN KEY (id_owner) REFERENCES accounts(id),
    CONSTRAINT fk_owned_games2 FOREIGN KEY (id_game) REFERENCES games(id),
    CONSTRAINT pk_owned_games PRIMARY KEY (id_owner,id_game)
)
/

CREATE TABLE auction (
    id_gamer INT NOT NULL,
    id_item INT NOT NULL,
    price NUMBER(5,2),
    added_at DATE,
    updated_at DATE,
    CONSTRAINT fk_auction_sellers_id_gamer FOREIGN KEY (id_gamer) REFERENCES accounts(id),
    CONSTRAINT fk_auction_items_id_item FOREIGN KEY (id_item) REFERENCES items(id),
    CONSTRAINT pk_auction_keys PRIMARY KEY (id_gamer,id_item)
)
/

