w_gamename VARCHAR2(50);
w_class VARCHAR2(50);
w_typeof VARCHAR2(50);
w_rarity VARCHAR2(50);
w_launch DATE;
w_wear VARCHAR2(50);
aux int;
aux1 int;
aux2 int;
aux3 number;
BEGIN
    DBMS_OUTPUT.PUT_LINE('Incepem');
    FOR v_i IN 1..400 LOOP
        w_user := user_names(v_i);
        w_email := replace (lower(w_user), ' ', '');
        IF (TRUNC(DBMS_RANDOM.VALUE(0,2))=0) then w_email := w_email ||'@gmail.com';
        ELSE w_email := w_email ||'@yahoo.com';
        END IF;
        w_pass := DBMS_RANDOM.STRING('X', 10);
        insert into accounts values(v_i, w_user,w_pass, w_email,0,sysdate,sysdate);
    END LOOP;
    FOR v_i IN 1..723 LOOP
        w_user := item_names(v_i);
        w_class := classes(TRUNC(DBMS_RANDOM.VALUE(0,classes.count))+1);
        w_typeof := typess(TRUNC(DBMS_RANDOM.VALUE(0,typess.count))+1);
        w_wear := wear(TRUNC(DBMS_RANDOM.VALUE(0,wear.count))+1);
        w_rarity := rarities(TRUNC(DBMS_RANDOM.VALUE(0,rarities.count))+1);
       INSERT INTO items VALUES(v_i,w_user,w_class,w_typeof,w_wear,w_rarity,dbms_random.value(1,1000),sysdate,sysdate);
    END LOOP;
    FOR v_i IN 1..700 LOOP
        w_gamename := game_names(v_i);
        w_launch := to_date('2000-01-01', 'yyyy-mm-dd')+trunc(DBMS_RANDOM.VALUE(1,6570));
        INSERT INTO games VALUES(v_i,w_gamename,dbms_random.value(1,1000),w_launch,sysdate,sysdate);
    END LOOP;
    aux:=TRUNC(DBMS_RANDOM.VALUE(0,699))+1;
    aux1 := TRUNC(DBMS_RANDOM.VALUE(1,400));
    aux2 := TRUNC(DBMS_RANDOM.VALUE(1,723));
    insert into auction values(aux1,aux2,dbms_random.value(1,1000),sysdate,sysdate);
    FOR v_i in 1..aux LOOP
        aux1 := TRUNC(DBMS_RANDOM.VALUE(1,400));
        aux2 := TRUNC(DBMS_RANDOM.VALUE(1,723));
        select count(*) into aux3 from auction where id_gamer = aux1 and id_item = aux2;
        if aux3 = 0 then
            insert into auction values(aux1,aux2,dbms_random.value(1,1000),sysdate,sysdate);
        end if;
    END LOOP;
    FOR v_i IN 1..700 LOOP
        aux1 := TRUNC(DBMS_RANDOM.VALUE(1,700));
        aux2 := TRUNC(DBMS_RANDOM.VALUE(1,723));
        select count(*) into aux3 from specific_items where id_game = aux1 and id_item = aux2;
        if aux3 = 0 then
            insert into specific_items values(aux1,aux2,sysdate,sysdate);
        end if;
    END LOOP;
    FOR v_i in 1..aux LOOP
        aux1 := TRUNC(DBMS_RANDOM.VALUE(1,400));
        aux2 := TRUNC(DBMS_RANDOM.VALUE(1,723));
        select count(*) into aux3 from owned_items where id_owner = aux1 and id_item = aux2;
        if aux3 = 0 then
            insert into owned_items values(aux1,aux2,sysdate,sysdate);
        end if;
    END LOOP;
    FOR v_i in 1..aux LOOP
        aux1 := TRUNC(DBMS_RANDOM.VALUE(1,400));
        aux2 := TRUNC(DBMS_RANDOM.VALUE(1,700));
        select count(*) into aux3 from owned_games where id_owner = aux1 and id_game = aux2;
        if aux3 = 0 then
            insert into owned_games values(aux1,aux2,sysdate,sysdate);
        end if;
    END LOOP;
END;
